drop sequence if exists seq_pericias_no_armas; 

CREATE SEQUENCE seq_pericias_no_armas
    START WITH 1
    INCREMENT BY 1
    NO MAXVALUE
    NO CYCLE;

drop table if exists pericias_no_armas;

create table pericias_no_armas(id int primary key,
nombre varchar2(100),
casillas_requeridas int,
habilidad varchar2(25),
modificador int default 0,
id_categoria int );















